import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';


function Header() {
    const navigate = useNavigate();
    const token = localStorage.getItem('token');
    let userRole = null;

    if (token) {
        try {
            const decoded = jwtDecode(token);
            userRole = decoded.roles[0]; // Nehme an, dass die Rolle im ersten Eintrag der `roles`-Liste ist
        } catch (error) {
            console.error('Invalid token');
        }
    }

    const handleLogout = () => {
        localStorage.removeItem('token'); // Entferne das Token
        navigate('/login'); // Weiterleitung zur Login-Seite
    };

    return (
        <header className="header">
            <div className="container">
                <h1 className="logo">
                    <Link to="/" className="logo-link">
                        Chatbot Dashboard
                    </Link>
                </h1>
                <nav className="nav">
                    {userRole === 'ADMIN' && (
                        <Link to="/admin" className="nav-link">
                            Admin Dashboard
                        </Link>
                    )}
                    {userRole === 'ADVANCED_USER' && (
                        <Link to="/advanced-user" className="nav-link">
                            Advanced Dashboard
                        </Link>
                    )}
                    <Link to="/profile" className="nav-link">
                        Profil
                    </Link>
                    <Link to="/help" className="nav-link">
                        Hilfe
                    </Link>
                    <button onClick={handleLogout} className="logout-button">
                        Logout
                    </button>
                </nav>
            </div>
        </header>
    );
}

export default Header;
